- (instancetype) init {
  if (self = [super init]) {
    // _impl에 Swift 구현체 인스턴스를 생성
    _impl = [RCTNativeLocalStorageImpl new]; // @implementation의 _impl 타입과 일치시켜야함
  }
  return self;
}

